{extend name="public/container"}{block name="head_top"}<link href="{__ADMIN_PATH}plug/umeditor/themes/default/css/umeditor.css" type="text/css" rel="stylesheet"><link href="{__ADMIN_PATH}module/wechat/news/css/style.css" type="text/css" rel="stylesheet"><link href="{__FRAME_PATH}css/plugins/chosen/chosen.css" rel="stylesheet"><script type="text/javascript" src="{__ADMIN_PATH}plug/umeditor/third-party/jquery.min.js"></script><script type="text/javascript" src="{__ADMIN_PATH}plug/umeditor/third-party/template.min.js"></script><script type="text/javascript" charset="utf-8" src="{__ADMIN_PATH}plug/umeditor/umeditor.config.js"></script><script type="text/javascript" charset="utf-8" src="{__ADMIN_PATH}plug/umeditor/umeditor.min.js"></script><script src="{__ADMIN_PATH}frame/js/ajaxfileupload.js"></script><script src="{__ADMIN_PATH}plug/validate/jquery.validate.js"></script><script src="{__FRAME_PATH}js/plugins/chosen/chosen.jquery.js"></script>{/block}{block name="content"}<div class="row" style="width: 100%">   <div class="col-sm-12">       <div class="col-sm-2 panel panel-default news-left">               <div class="panel-heading">店铺列表</div>               <div class="panel-body news-box type-all" >                   {if condition="$news['store_logo']"}                   <div class="news-item transition active news-image" style="margin-bottom: 20px;background-image:url({$news['store_logo']})">                   {else/}                   <div class="news-item transition active news-image" style="margin-bottom: 20px;background-image:url('/public/system/module/wechat/news/images/image.png')">                   {/if}                       <input type="hidden" name="new_id" value="{$news.id}" class="new-id">                   </div>               </div>       </div><!-- col-sm-10 panel panel-default news-right -->       <div class="col-sm-12 panel panel-default" >               <div class="panel-heading">店铺内容编辑</div>               <div class="panel-body">                   <form class="form-horizontal" id="signupForm">                       <div class="form-group">                           <div class="col-md-12">                               <div class="input-group">                                   <span class="input-group-addon">店铺名称</span>                                   <input maxlength="255" placeholder="请在这里输入店铺名称" name="store_name" class="layui-input" id="store_name" value="{$news.store_name}">                                   <input type="hidden" name="id" value="{$news.id}">                               </div>                           </div>                       </div>                       <div class="form-group">                           <div class="col-md-12">                               <div class="input-group">                                   <span class="input-group-addon">店铺地址</span>                                   <input maxlength="64" placeholder="请在这里输入店铺地址" name="store_address" class="layui-input" id="store_address" value="{$news.store_address}" onclick="createFrame('选择地址','{:Url('location')}',{w:1100,h:500})">                                   <input type="hidden" id="lng" name="lng" value="{$news.lng}">                                   <input type="hidden" id="lat" name="lat" value="{$news.lat}">                                   <input type="hidden" name="id" value="{$news.id}">                               </div>                           </div>                       </div>                       <div class="form-group">                           <div class="col-md-12">                               <div class="input-group">                                   <span class="input-group-addon">店铺分类</span>                                   <?php if(empty($all)){?>                                      <select data-placeholder="请先添加店铺分类" class="chosen-select"  style="width:100%;" tabindex="4" name="type">                                   <?php }else{ ?>                                      <select data-placeholder="选择店铺分类" class="chosen-select"  style="width:100%;" tabindex="4" name="type" {eq name="$select" value="1"}disabled="disabled"{/eq}>                                   <?php }?>                                       {volist name="all" id="vo" key="k"}                                          {if condition="$key eq $cid"}                                          <option value="{$key}" selected hassubinfo="true" >{$vo}</option>                                          {elseif condition="in_array($key,$news['cid'])"}                                          <option value="{$key}" selected hassubinfo="true" >{$vo}</option>                                          {else/}                                          <option value="{$key}" hassubinfo="true" >{$vo}</option>                                        {/if}                                       {/volist}                                   </select></div>                           </div>                       </div>                       <div class="form-group">                           <div class="col-md-12">                               <div class="form-control" style="height:auto">                                   <label style="color:#ccc">店铺封面大图片设置</label>                                   <div class="row nowrap">                                       <div class="col-xs-3" style="width:160px">                                           {if condition="$news['store_logo']"}                                           <div class="upload-image-box transition image_img" style="height: 80px;background-repeat:no-repeat;background-size:contain;background-image:url({$news['store_logo']})">                                               <input value="" type="hidden" name="local_url">                                           </div>                                           {else/}                                           <div class="upload-image-box transition image_img" style="height: 80px;background-repeat:no-repeat;background-size:contain;background-image:url('/public/system/module/wechat/news/images/image.png')">                                               <input value="" type="hidden" name="local_url">                                           </div>                                           {/if}                                       </div>                                       <div class="col-xs-6">                                           <input type="file" class="upload" name="image" style="display: none;" id="image" />                                           <br>                                           <a class="btn btn-sm add_image upload_span">上传图片</a>                                           <br>                                           <br>                                       </div>                                   </div>                                   <input type="hidden" name="image" id="store_logo" value="{$news['store_logo']}"/>                                   <p class="help-block" style="margin-top:10px;color:#ccc">封面大图片建议尺寸：900像素 * 500像素</p>                               </div>                           </div>                       </div>                       <div class="form-group">                           <div class="col-md-12">                               <div class="input-group">                                   <span class="input-group-addon">手机号</span>                                   <input maxlength="11" placeholder="请输入手机号" name="mobile" class="layui-input" id="mobile" value="{$news.mobile}">                               </div>                           </div>                       </div>                       <div class="form-group">                           <div class="col-md-12">                               <div class="input-group">                                   <span class="input-group-addon">密码</span>                                   <input maxlength="8" placeholder="请输入密码" name="password" class="layui-input" id="password" value="{$news.password}">                               </div>                           </div>                       </div>                       <div class="form-group">                           <div class="col-md-12">                               <div class="input-group">                                   <span class="input-group-addon">姓名</span>                                   <input maxlength="8" placeholder="请输入姓名" name="name" class="layui-input" id="name" value="{$news.name}">                               </div>                           </div>                       </div>                       <div class="form-group">                           <div class="col-md-12">                               <div class="input-group">                                   <span class="input-group-addon">身份证号</span>                                   <input maxlength="8" placeholder="请输入身份证号" name="card_no" class="layui-input" id="card_no" value="{$news.card_no}">                               </div>                           </div>                       </div>                       <div class="form-group">                           <div class="col-md-12">                               <div class="input-group">                                   <span class="input-group-addon">卡号</span>                                   <input maxlength="8" placeholder="请输入卡号" name="bank_no" class="layui-input" id="bank_no" value="{$news.bank_no}">                               </div>                           </div>                       </div>                       <div class="form-group">                           <div class="col-md-12">                               <div class="input-group">                                   <span class="input-group-addon">所属银行</span>                                   <input maxlength="8" placeholder="请输入所属银行" name="bank" class="layui-input" id="bank" value="{$news.bank}">                               </div>                           </div>                       </div>                       <div class="form-group">                           <div class="col-md-12">                               <div class="input-group">                                   <span class="input-group-addon">开户支行</span>                                   <input maxlength="8" placeholder="请输入开户支行" name="bank_name" class="layui-input" id="bank_name" value="{$news.bank_name}">                               </div>                           </div>                       </div>                       <div class="form-group">                           <div class="col-md-12">                               <div class="input-group">                                   <span class="input-group-addon">银行开户地址</span>                                   <input maxlength="8" placeholder="请输入银行开户地址" name="bank_address" class="layui-input" id="bank_address" value="{$news.bank_address}">                               </div>                           </div>                       </div>                       <div class="form-actions">                           <div class="row">                               <div class="col-md-offset-4 col-md-9">                                   <button type="button" class="btn btn-w-m btn-info save_news">保存</button>                               </div>                           </div>                       </div>                   </form>               </div>       </div>   </div></div>{/block}{block name="script"}<script>            function createFrame(title,src,opt){                opt === undefined && (opt = {});                return layer.open({                    type: 2,                    title:title,                    area: [(opt.w || 700)+'px', (opt.h || 650)+'px'],                    fixed: false, //不固定                    maxmin: true,                    moveOut:false,//true  可以拖出窗外  false 只能在窗内拖                    anim:5,//出场动画 isOutAnim bool 关闭动画                    offset:'auto',//['100px','100px'],//'auto',//初始位置  ['100px','100px'] t[ 上 左]                    shade:0,//遮罩                    resize:true,//是否允许拉伸                    content: src,//内容                    move:'.layui-layer-title'                });            }            function changeIMG(index,pic){                $(".image_img").css('background-image',"url("+pic+")");                $(".active").css('background-image',"url("+pic+")");                $('#store_logo').val(pic);            };            /**             * 上传图片             * */            $('.upload_span').on('click',function (e) {//                $('.upload').trigger('click');                createFrame('选择图片','{:Url('widget.images/index')}?fodder=image');            })            /**             * 编辑器上传图片             * */            $('.edui-icon-image').on('click',function (e) {//                $('.upload').trigger('click');                createFrame('选择图片','{:Url('widget.images/index')}?fodder=image');            })            /**            * 提交图文            * */            $('.save_news').on('click',function(){                var list = {};                list.store_name = $('#store_name').val();/* 标题 */                list.store_address = $('#store_address').val();/* 作者 */                list.lng = $('#lng').val();/* 作者 */                list.lat = $('#lat').val();/* 作者 */                list.store_logo = $('#store_logo').val();/* 图片 */                list.mobile = $('#mobile').val();/* 内容 */                list.password = $('#password').val();/* 简介 */                list.name = $('#name').val();/* 原文链接 */                list.card_no = $('#card_no').val();/* 原文链接 */                list.bank_no = $('#bank_no').val();/* 原文链接 */                list.bank = $('#bank').val();/* 原文链接 */                list.bank_name = $('#bank_name').val();/* 原文链接 */                list.bank_address = $('#bank_address').val();/* 原文链接 */                list.cid = $('.chosen-select').val();                if(list.store_name == ''){                    console.log("aa")                    $eb.message('error','请输入店铺名称');                    return false;                }                if(list.store_address == ''){                    $eb.message('error','请输入店铺地址');                    return false;                }                if(list.store_logo == ''){                    $eb.message('error','请添加店铺logo图片');                    return false;                }                if(list.mobile == ''){                    $eb.message('error','请输入手机号');                    return false;                }                if(list.password == ''){                    $eb.message('error','请输入密码');                    return false;                }                if(list.name == ''){                    $eb.message('error','请输入姓名');                    return false;                }                if(list.card_no == ''){                    $eb.message('error','请输入卡号');                    return false;                }                if(list.bank_no == ''){                    $eb.message('error','请输入银行卡号');                    return false;                }                if(list.bank == ''){                    $eb.message('error','请输入银行名称');                    return false;                }                if(list.bank_name == ''){                    $eb.message('error','请输入银行开户行');                    return false;                }                if(list.bank_address == ''){                    $eb.message('error','请输入银行开户行地址');                    return false;                }                var id = $('.type-all>.active>.new-id').val();                if(id != ''){                    list.id = id;                }                var data = {};                $.ajax({                    url:"{:Url('add_new')}",                    data:list,                    type:'post',                    dataType:'json',                    success:function(re){                        console.log(re)                        if(re.code == 200){                            data[re.data] = list;                            $('.type-all>.active>.new-id').val(re.data);                            $eb.message('success',re.msg);                            setTimeout(function (e) {                                parent.$(".J_iframe:visible")[0].contentWindow.location.reload();                                parent.layer.close(parent.layer.getFrameIndex(window.name));                            },600)                        }else{                            $eb.message('error',re.msg);                        }                    }                })            });            $('.article-add ').on('click',function (e) {                var num_div = $('.type-all').children('div').length;                if(num_div > 7){                  $eb.message('error','一组图文消息最多可以添加8个');                  return false;                }                var url = "/public/system/module/wechat/news/images/image.png";                html = '';                html += '<div class="news-item transition active news-image" style=" margin-bottom: 20px;background-image:url('+url+')">'                    html += '<input type="hidden" name="new_id" value="" class="new-id">';                    html += '<span class="news-title del-news">x</span>';                html += '</div>';                $(this).siblings().removeClass("active");                $(this).before(html);            })            $(document).on("click",".del-news",function(){                $(this).parent().remove();            })            $(document).ready(function() {                var config = {                    ".chosen-select": {},                    ".chosen-select-deselect": {allow_single_deselect: true},                    ".chosen-select-no-single": {disable_search_threshold: 10},                    ".chosen-select-no-results": {no_results_text: "沒有找到你要搜索的分类"},                    ".chosen-select-width": {width: "95%"}                };                for (var selector in config) {                    $(selector).chosen(config[selector])                }            })        </script>{/block}